import React, { Component } from 'react'

const Increment = Inheritor => {
  class ModifiedComponent extends Component {
      // Inheritance
      constructor (props) {
          super (props)
          this.state = {
            count : 0
          }
      }
      // Increment Function
      increment = () => {
        this.setState(prevState => {
          count: prevState.count + 1
        })
      }

      // Render Modified ModifiedComponent with Inheritances
      render () {
          return <Inheritor increment={ this.increment } count = { this.state.count }/>
      }
      render () {
        return ModifiedComponent
      }
  }
}

export default Increment;
