import { FETCH_POSTS, NEW_POST } from '../Actions/types'

const initialState = {
    items: [],
    item:{}
}

export default function (state = initialState, action) {
    console.log('saving state')
    switch(action.type) {

        case FETCH_POSTS: return {
            ...state, 
            items: action.payload
        }
        default: return state;
    }
}
