import React, { Component } from 'react'
import { Container } from 'reactstrap'

class Post extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             posts: []
        }
    }
    // insert posts before mount
    componentWillMount () {
        fetch()
        .then(res => res.json())
        .then(data => this.setState({
            posts: data
        }), ()=> console.log(this.state.posts) )
}

    render() {
        const postsItems = this.state.posts.map( post => (
            <div key={post.id}>
                <h3>{ post.title }</h3>
                <p>{post.body}</p>
            </div>
        ));
        return (
            <div>
                <Container>
                    <h1>Posts</h1>
                    { postsItems }
                </Container>
            </div>
        )
    }
}

export default Post;
