import React, { Component } from 'react'
import { Container, FormGroup } from 'reactstrap'

class PostForm extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            title:'',
            body:''
        }
    }
    // Form Handler
    ChangeHandler = (e) => {
        this.setState({
            [e.target.name]:e.target.value
        })
    }

    
    render() {
        const { title, body } = this.state;
        return (
            <Container>
                <h1>Add Post { title } { body }</h1>
                <form>
                    <FormGroup>
                        <input 
                            type="text" 
                            className="form-control"
                            onChange={this.ChangeHandler} 
                            name='title' 
                            placeholder="Title"
                            value={ title } />
                    </FormGroup>
                    <FormGroup>
                        <textarea 
                            type="text" 
                            className="form-control" 
                            onChange={this.ChangeHandler}
                            name='body' 
                            placeholder="Body"
                            value={ body } ></textarea>
                    </FormGroup>
                </form>
            </Container>
        )
    }
}

export default PostForm
