const Increment = Inheritor => {
  class ModifiedComponent extends Component {
    // Inheritance
    state = { count : 0}
    // Increment Method
    increment = () => {
      this.setState(prevState=> ({
        count: orevState.count + 1
      }))
    }
    render () {
      return <Inheritor increment={this.increment} count={this.state.count}/>
    }
  }
  return ModifiedComponent
}

export default Increment;
