import React, { Component } from 'react'

class Post extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             posts: []
        }
    }
    
    componentWillMount () {
        fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res => res.json())
        .then(data =>this.setState({ post: data }))
}

    render() {
        const postsItems = this.state.posts.map( post => (
            <div key={post.id}>
                <h3>{ post.title }</h3>
            </div>
        ));
        return (
            <div>
                <h1>Posts</h1>
                postsItems}
            </div>
        )
    }
}

export default Post;
