import React, { Component } from 'react'
import { Container } from 'reactstrap'

class Post extends Component {
    constructor (props) {
        super(props)
        this.state = {
            post: []
        }
    }
    // set state post before mount
    componentWillMount () {
        fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res=>res.json())
        .then(data => this.setState({ post:data }))
    }
    render() {
       const postItem = this.state.post.map(post => (
           <div key={post.id}>
               <h3>{post.title}</h3>
               <p>{post.body}</p>
           </div>
       ))
    
        return (
            <div>
               <Container>
                    <h1>Post</h1>
                   { post.item }
               </Container>
            </div>
        )
    }
}

export default Post;
